import React, { useState, useEffect } from 'react';
import axios from 'axios';

function RegistrationForm() {
  const [formData, setFormData] = useState({
    department: '',
    faculty: '',
    courseCode: '',
    courseName: ''
  });
  const [departments, setDepartments] = useState([]);
  const [faculties, setFaculties] = useState([]);

  useEffect(() => {
    const fetchDepartments = async () => {
      const { data } = await axios.get('/api/departments');
      setDepartments(data);
      if (data.length > 0) {
        setFormData(f => ({ ...f, department: data[0].name }));
      }
    };
    fetchDepartments();
  }, []);

  useEffect(() => {
    const fetchFaculties = async () => {
      const { data } = await axios.get(`/api/faculties?department=${formData.department}`);
      setFaculties(data);
      if (data.length > 0) {
        setFormData(f => ({ ...f, faculty: data[0].name }));
      }
    };
    if (formData.department) fetchFaculties();
  }, [formData.department]);

  const handleChange = (event) => {
    const { name, value } = event.target;
    setFormData(prevState => ({
      ...prevState,
      [name]: value
    }));
  };

  const handleSubmit = async (event) => {
    event.preventDefault();
    try {
      const response = await axios.post('/api/courses/register', {
        ...formData,
        open_elective: formData.courseName
      });
      console.log(response.data);
      alert("Registration successful!");
    } catch (error) {
      console.error('Failed to register the course:', error);
      alert("Failed to register the course");
    }
  };

  return (
    <form onSubmit={handleSubmit}>
      <h2>Faculty Registration For New Course</h2>
      <div>
        <label>Department</label>
        <select name="department" value={formData.department} onChange={handleChange}>
          {departments.map(d => <option key={d.name} value={d.name}>{d.name}</option>)}
        </select>
      </div>
      <div>
        <label>Faculty</label>
        <select name="faculty" value={formData.faculty} onChange={handleChange}>
          {faculties.map(f => <option key={f.name} value={f.name}>{f.name}</option>)}
        </select>
      </div>
      <div>
        <label>Course Code</label>
        <input
          type="text"
          name="courseCode"
          value={formData.courseCode}
          onChange={handleChange}
        />
      </div>
      <div>
        <label>Course Name</label>
        <input
          type="text"
          name="courseName"
          value={formData.courseName}
          onChange={handleChange}
        />
      </div>

      <button type="submit">Register</button>
    </form>
  );
}

export default RegistrationForm;
