import React, { useState } from 'react';
import './FacultyPage.css';
import Footer from './Footer';

function FacultyPage() {
  const [courses, setCourses] = useState([
    { id: 1, name: 'Course 1', selected: false, faculty: '' },
    { id: 2, name: 'Course 2', selected: false, faculty: '' },
    { id: 3, name: 'Course 3', selected: false, faculty: '' },
    { id: 4, name: 'Course 4', selected: false, faculty: '' },
    { id: 5, name: 'Course 5', selected: false, faculty: '' },
  ]);
  const [submissionStatus, setSubmissionStatus] = useState('');

  const handleCourseSelect = (courseId) => {
    setCourses(courses.map(course => {
      if (course.id === courseId) {
        return { ...course, selected: !course.selected };
      }
      return course;
    }));
  };

  const handleCourseNameChange = (e, courseId) => {
    const newName = e.target.value;
    setCourses(courses.map(course => {
      if (course.id === courseId) {
        return { ...course, name: newName };
      }
      return course;
    }));
  };

  const handleFacultyNameChange = (e, courseId) => {
    const newFaculty = e.target.value;
    setCourses(courses.map(course => {
      if (course.id === courseId) {
        return { ...course, faculty: newFaculty };
      }
      return course;
    }));
  };

  const handleSubmit = async () => {
    const selectedCourses = courses.filter(course => course.selected);
    try {
      const response = await fetch('/api/courses/submit', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json'
        },
        body: JSON.stringify(selectedCourses)
      });
      if (response.ok) {
        console.log('Courses submitted successfully');
        setSubmissionStatus('SUBMITTED!');
        // Clear the selection after successful submission
        setCourses(courses.map(course => ({ ...course, selected: false })));
      } else {
        console.error('Failed to submit courses');
      }
    } catch (error) {
      console.error('Error submitting courses:', error);
    }
  };

  return (
    <div className="FacultyPage">
      
      <h1>Department of Computer Science And Engineering</h1>

      <table className="course-table">
        <thead>
          <tr>
            <th>Course Name</th>
            <th>Faculty Name</th>
          </tr>
        </thead>
        <tbody>
          {courses.map(course => (
            <tr key={course.id}>
              <td>
                <input
                  type="text"
                  id={`courseName-${course.id}`}
                  name={`courseName-${course.id}`} // Add name attribute
                  value={course.name}
                  onChange={(e) => handleCourseNameChange(e, course.id)}
                />
                <input
                  type="checkbox"
                  id={`course-${course.id}`}
                  checked={course.selected}
                  onChange={() => handleCourseSelect(course.id)}
                />
              </td>
              <td>
                <input
                  type="text"
                  id={`facultyName-${course.id}`}
                  name={`facultyName-${course.id}`} // Add name attribute
                  value={course.faculty}
                  onChange={(e) => handleFacultyNameChange(e, course.id)}
                />
              </td>
            </tr>
          ))}
        </tbody>
      </table>

      <button onClick={handleSubmit}>
        Submit
      </button>
      {submissionStatus && <p>{submissionStatus}</p>}
      <Footer />
    </div>
  );
}

export default FacultyPage;
