import React, { useState, useEffect } from 'react';

function RegistrationForm() {
  const [formData, setFormData] = useState({
    department: '',
    faculty: '',
    courseCode: '',
    courseName: ''
  });
  const [departments] = useState([
    { id: 1, name: 'Computer Science' },
    { id: 2, name: 'Electrical Engineering' },
    { id: 3, name: 'Mechanical Engineering' },
    { id: 4, name: 'Chemistry' }
  ]);
  const [faculties, setFaculties] = useState([]);

  // Update faculties based on the department selected
  useEffect(() => {
    const facultiesByDepartment = {
      'Computer Science': [
        { id: 1, name: 'Prof. Alice' },
        { id: 2, name: 'Prof. Bob' }
      ],
      'Electrical Engineering': [
        { id: 3, name: 'Prof. Charlie' },
        { id: 4, name: 'Prof. Dana' }
      ],
      'Mechanical Engineering': [
        { id: 5, name: 'Prof. Eve' },
        { id: 6, name: 'Prof. Frank' }
      ],
      'Chemistry': [
        { id: 7, name: 'Prof. Grace' },
        { id: 8, name: 'Prof. Heidi' }
      ]
    };
    if (formData.department) {
      setFaculties(facultiesByDepartment[formData.department]);
    } else {
      setFaculties([]);
    }
  }, [formData.department]);

  const handleChange = (event) => {
    const { name, value } = event.target;
    setFormData(prevState => ({
      ...prevState,
      [name]: value
    }));
  };

  const handleSubmit = (event) => {
    event.preventDefault();
    console.log('Submitting Form:', formData);
    alert(`Registration for ${formData.courseName} (${formData.courseCode}) under ${formData.faculty} of ${formData.department} department`);
  };

  return (
    <form onSubmit={handleSubmit}>
      <h2>Faculty Registration For New Course</h2>
      <div>
        <label>Department</label>
        <select name="department" value={formData.department} onChange={handleChange}>
          <option value="">Select a department</option>
          {departments.map(dept => (
            <option key={dept.id} value={dept.name}>{dept.name}</option>
          ))}
        </select>
      </div>
      <div>
        <label>Faculty</label>
        <select name="faculty" value={formData.faculty} onChange={handleChange} disabled={!formData.department}>
          <option value="">Select a faculty</option>
          {faculties.map(faculty => (
            <option key={faculty.id} value={faculty.name}>{faculty.name}</option>
          ))}
        </select>
      </div>
      <div>
        <label>Course Code</label>
        <input
          type="text"
          name="courseCode"
          value={formData.courseCode}
          onChange={handleChange}
        />
      </div>
      <div>
        <label>Course Name</label>
        <input
          type="text"
          name="courseName"
          value={formData.courseName}
          onChange={handleChange}
        />
      </div>
      <button type="submit">Register</button>
    </form>
  );
}

export default RegistrationForm;
